  selectInput = event => {
    event.target.focus()
    event.target.select()
  }

  handleChange = event => {
    this.setState({ textValue: event.target.value })
  }

  deleteItem = () => {
    this.setState({ isDeleted: true })
    this.toggleSelect()
    setTimeout(() => {
      this.setState({ isAlive: false })
    }, 150)
  }

