                <div className="text-div-style">
               <p>Num persone: {num_soggiornanti} Costo: {costo} </p>          
               <p>Partenza-Ritorno: {new Date(data_partenza).toLocaleDateString()},  {new Date(data_ritorno).toLocaleDateString()}</p>
               <p>CF Soggiornante: {ref_soggiornante}</p>
           
               </div>
              </ListItemTextSecond>
            </ListItemInfo>
        
            {hasActions && (
              <ListItemSelect showSelect={showSelect}>
                <div
                  className="listitem__select__text"
                  onClick={this.toggleSelect}
                >
                  Azioni
                  <SelectArrowDown>
                    <svg
                      x="0px"
                      y="0px"
                      viewBox="0 0 100 125"
                      style={{ enableBackground: 'new 0 0 100 100' }}
                    >
                      <g>
                        <polygon points="64.4,40.4 51.1,54 37.6,40.7 34.8,43.6 51.2,59.7 67.3,43.2  " />
                      </g>
                    </svg>
                  </SelectArrowDown>
                </div>
                {showSelect && (
                  <div
                    className={`listitem__select__list ${
                      showSelect ? 'listitem__select__list--show' : ''
                    }`}
                  >
                    <div className="listitem__select__list__item">
                      <div className="LinkList" //qua va modificato in modo che permetta di accettare la prenotazione                       
                      >
                      <p onDoubleClick={this.prova('s')}>Accetta</p>
                      </div>
                    </div>
                    <div
                      className="listitem__select__list__item"
                      onClick={this.deleteItem}
                    >
                    Rifiuta
                    </div>
                  </div>
                )}
              </ListItemSelect>
            )}
          </ListItemContent>
        </ListItemWrapper>
      </ThemeProvider>
    )
  }
}

export default ListItemPrenotazioni
