import React from "react";
import { useContext } from "react";
import { CasaVacanzaContext } from "../CasaVacanza/context";
import Title from "./title";

import './CaseVacanzaFiltro.css'
// get all unique values
/*const getUnique = (items, value) => {
    if(items)
  return [...new Set(items.map(item => item[value]))];
};*/

class CaseVacanzaFilter extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      servizi: this.props.servizi ? this.props.servizi : [],
      case: this.props.case ? this.props.case : [],
      type: '',
      localita: '',
      posti: this.props.posti ? this.props.posti : 1,
      tariffa: 1000,
      minTariffa: 0,
      maxTariffa: 1000,
      minSize: '',
      maxSize: '',
      error: false,
      errorMessage: ''
    }
  }

  handleChange = (e) => {
    this.setState({ [e.target.id]: e.target.value });

    this.props.onChange(this.state);
  }

  render() {
    return (
      <section className="filter-container">
        <form className="filter-form">
          {/* select type */}
          <div className="form-group">
            <label htmlFor="type">CasaVacanza type</label>
            <select
              name="type"
              id="type"
              onChange={this.handleChange}
              className="form-control"
            >
            </select>
          </div>
          {/* end of select type */}
          <div className="form-group">
            <label htmlFor="localita">Località</label>
            <select
              name="localita"
              id="localita"
              onChange={this.handleChange}
              className="form-control"
            >
            </select>
          </div>
          {/* guests  */}
          <div className="form-group">
            <label htmlFor="posti">Ospiti</label>
            <select
              name="posti"
              id="posti"
              onChange={this.handleChange}
              className="form-control"
              value={this.state.posti}
            >
              <option></option>
              <option value="1">1</option>
              <option value="2">2</option>
              <option value="3">3</option>
              <option value="4">4</option>
              <option value="5">5</option>
              <option value="6">6</option>
              <option value="7">7</option>
              <option value="8">8</option>
              <option value="9">9</option>
              <option value="10">10</option>
            </select>
          </div>
          {/* end of guests */}
          {/* casaVacanza price */}
          <div className="form-group">
            <label htmlFor="tariffa">prezzo CasaVacanza €{this.state.tariffa}</label>
            <input
              type="range"
              name="tariffa"
              min={this.state.minTariffa}
              max={this.state.maxTariffa}
              id="tariffa"
              onChange={this.handleChange}
              className="form-control"
              defaultValue = {this.state.tariffa}
            />
          </div>
          {/* end of casaVacanza price*/}
          {/* size */}
          <div className="form-group-size">
            <label htmlFor="price">room size </label>
            <div className="size-inputs">
              <input
                type="number"
                name="minSize"
                id = "minSize"
                onChange={this.handleChange}
                className="size-input"
                placeholder = "min"
                min = '1'
                max = '10'
              />
              <input
                type="number"
                name="maxSize"
                id = "maxSize"
                onChange={this.handleChange}
                className="size-input"
                placeholder = "max"
                min = '1'
                max = '10'
              />
            </div>
          </div>
          {/* end of select type */}
        </form>
        {/* extras */}
        <div className="form-group-extra">
          {this.props.servizi.map(item => {
            return(
              <div className="single-extra">
                <input
                  type="checkbox"
                  name={item.servizio}
                  id={item.servizio}
                  onChange={this.handleChange}
                />
                <label htmlFor={item.servizio}>{item.servizio}</label>
              </div>
            )
          })}
          </div>
          {/* end of extras type */}
      </section>
    );
  }
};

