    </Form.Row>
    <button type = 'button' className="bottoniSceltaModifica" onClick = {this.onSubmit}>
                Carica foto
              </button>
  </Form.Group>

  
                  </Card.Body>
                </Accordion.Collapse>
              </Card>
            </Accordion>
          </div>
        </div>
